create database codequest;
use codequest;
create table player (idPlayer int primary key not null auto_increment,
nome varchar(200),
vida int not null,
acertos int not null,
erros int not null,
pontos int not null);
describe player;
describe questoes;
create table questoes(idquestao int primary key not null,
enunciado varchar(1000) not null,
alternativa1 varchar(1000) not null,
alternativa2 varchar(1000) not null,
alternativa3 varchar(1000) not null,
alternativa4 varchar(1000) not null);
;
drop table questoes;
